        </div>
      </section>

      {/* Grow Your Business CTA */}
      <section id="grow-your-business" className="bg-gradient-to-r from-blue-600 to-blue-800 py-20 px-6 text-center" data-animate>
        <div className="relative max-w-4xl mx-auto p-12 rounded-2xl overflow-hidden group">
          <div className="absolute inset-0 bg-gradient-to-tr from-blue-300 via-blue-400 to-blue-600 opacity-30 blur-3xl animate-pulse"></div>
          <div className="relative bg-white rounded-2xl shadow-lg p-10 transition-transform transform group-hover:scale-105 group-hover:shadow-2xl">
            <h2 className="text-3xl font-bold mb-6 text-blue-700">Grow Your Business With Relam.ai</h2>
