import React from 'react';

function HowRelamWorks() {
  const steps = [
    {
      title: "Ingest Internal Docs",
      description: "Learn from your sales decks and internal playbooks.",
      icon: "üìÑ",
    },
    {
      title: "Proprietary External Data",
      description: "Signals from trusted external partners and live web.",
      icon: "üîí",
    },
    {
      title: "ML Summarization",
      description: "Summarize, prioritize, and filter insights automatically.",
      icon: "‚öôÔ∏è",
    },
    {
      title: "Slack & CRM Delivery",
      description: "Send signals into Slack channels and CRM workflows.",
      icon: "üöÄ",
    },
  ];

  return (
    <section id="how-relam-works" className="px-6 py-24 bg-white text-center" data-animate>
      <h2 className="text-4xl font-bold text-[#111827] mb-6">How Relam.ai Powers Smarter Growth</h2>
      <p className="max-w-2xl mx-auto text-[#4b5563] mb-12">
        Relam learns from your decks, taps proprietary external data, applies AI/ML, and surfaces contextual signals.
      </p>
      <div className="grid md:grid-cols-4 gap-10 max-w-6xl mx-auto text-left">
        {steps.map((step, index) => (
          <div key={index} className="bg-white p-6 rounded-xl shadow-md hover:shadow-lg text-center">
            <div className="text-4xl mb-4">{step.icon}</div>
            <h4 className="text-lg font-semibold mb-2 text-[#222222]">{step.title}</h4>
            <p className="text-sm text-[#555555]">{step.description}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default HowRelamWorks;
