import React, { useEffect, useState } from 'react';
import { motion } from 'framer-motion';

// --- Message Component ---
const Message = ({ time, title, bullets }) => (
  <motion.div
    variants={{
      hidden: { opacity: 0, y: 20 },
      visible: { opacity: 1, y: 0 },
    }}
    transition={{ duration: 0.5, ease: 'easeOut' }}
    className="relative flex items-start gap-3 bg-white p-4 rounded-lg shadow-md hover:shadow-lg transition-all w-full"
  >
    {/* Pulsing New Message Ping */}
    <motion.div
      initial={{ opacity: 0, scale: 0 }}
      animate={{ opacity: 1, scale: [1, 1.4, 1] }}
      transition={{ delay: 0.3, duration: 1, repeat: Infinity, ease: 'easeInOut' }}
      className="absolute top-2 right-2 w-3 h-3 bg-[#22c55e] rounded-full shadow-lg"
    ></motion.div>

    {/* Avatar */}
    <div className="flex-shrink-0">
      <div className="w-8 h-8 rounded-full bg-[#0052cc] flex items-center justify-center text-white font-bold text-sm">
        R
      </div>
    </div>

    {/* Content */}
    <div className="text-left flex-1">
      <div className="flex items-center gap-2 mb-1">
        <p className="font-bold text-[#111827] text-sm">Relam Insights</p>
        <span className="text-xs text-gray-400">{time}</span>
      </div>
      <div className="text-[#374151] text-sm">
        <p className="font-semibold mb-1">{title}</p>
        <ul className="list-disc list-inside space-y-1">
          {bullets.map((bullet, idx) => (
            <li key={idx}>{bullet}</li>
          ))}
        </ul>
      </div>
    </div>
  </motion.div>
);

// --- Typing Indicator Component ---
const TypingIndicator = () => (
  <motion.div
    initial={{ opacity: 0 }}
    animate={{ opacity: 1 }}
    transition={{ duration: 0.5 }}
    className="flex items-center gap-2 bg-white p-4 rounded-lg shadow-md w-full max-w-xl mx-auto mb-4"
  >
    <div className="flex-shrink-0">
      <div className="w-8 h-8 rounded-full bg-[#0052cc] flex items-center justify-center text-white font-bold text-sm">
        R
      </div>
    </div>

    <div className="text-left flex-1">
      <div className="flex items-center gap-2 mb-1">
        <p className="font-bold text-[#111827] text-sm">Relam Insights</p>
        <span className="text-xs text-gray-400">typing...</span>
      </div>
      <div className="flex gap-1">
        <motion.div animate={{ opacity: [0.3, 1, 0.3] }} transition={{ repeat: Infinity, duration: 1 }} className="w-2 h-2 bg-gray-400 rounded-full"></motion.div>
        <motion.div animate={{ opacity: [0.3, 1, 0.3] }} transition={{ repeat: Infinity, duration: 1, delay: 0.2 }} className="w-2 h-2 bg-gray-400 rounded-full"></motion.div>
        <motion.div animate={{ opacity: [0.3, 1, 0.3] }} transition={{ repeat: Infinity, duration: 1, delay: 0.4 }} className="w-2 h-2 bg-gray-400 rounded-full"></motion.div>
      </div>
    </div>
  </motion.div>
);

// --- Slack Workspace Mockup ---
function SlackWorkspaceMockup() {
  const steps = [
    { type: 'typing' },
    {
      type: 'message',
      time: "9:34 AM",
      title: "New Competitor Alert",
      bullets: [
        "Acme Corp launched a new product line.",
        "Pricing changes detected on BetaCorp's website."
      ]
    },
    { type: 'typing' },
    {
      type: 'message',
      time: "9:37 AM",
      title: "Customer Churn Risk Detected",
      bullets: [
        "2 strategic accounts showed decreased engagement last week.",
        "Recommended proactive retention outreach."
      ]
    },
    { type: 'typing' },
    {
      type: 'message',
      time: "9:41 AM",
      title: "Expansion Opportunity",
      bullets: [
        "GammaCorp added 3 new regions in their CRM.",
        "Potential upsell detected in enterprise segment."
      ]
    }
  ];

  const [visibleSteps, setVisibleSteps] = useState([]);
  const [currentStep, setCurrentStep] = useState(0);

  useEffect(() => {
    if (currentStep < steps.length) {
      const timer = setTimeout(() => {
        setVisibleSteps(prev => [...prev, steps[currentStep]]);
        setCurrentStep(prev => prev + 1);
      }, steps[currentStep].type === 'typing' ? 1200 : 800);
      return () => clearTimeout(timer);
    }
  }, [currentStep, steps]);

  return (
    <div className="w-full bg-[#f9fafb] rounded-2xl shadow-lg overflow-hidden">
      <div className="flex items-center justify-between px-6 py-4 border-b border-gray-200">
        <div className="font-semibold text-gray-700"># competitive-intelligence</div>
        <div className="bg-gray-100 p-2 rounded text-gray-400 text-sm">Search...</div>
      </div>
      <div className="flex flex-col gap-6 p-6">
        {visibleSteps.map((step, idx) =>
          step.type === 'typing' ? (
            <TypingIndicator key={`typing-${idx}`} />
          ) : (
            <Message key={`message-${idx}`} {...step} />
          )
        )}
      </div>
    </div>
  );
}

// --- How Relam Works Main Section ---
function HowRelamWorks() {
  const sources = [
    { icon: 'üìä', label: 'Property Data' },
    { icon: 'üåê', label: 'Web Signals' },
    { icon: 'üßë‚Äçüíº', label: 'Customer Lists' },
    { icon: 'üìÑ', label: 'Internal Docs' },
  ];

  return (
    <section id="how-it-works" className="py-32 px-6 relative bg-white overflow-hidden">
      <div className="max-w-6xl mx-auto text-center">

        <h2 className="text-4xl font-bold mb-20 text-[#111827]">How Relam.ai Works</h2>

        {/* Sources */}
        <div className="flex flex-wrap justify-center gap-10 mb-12">
          {sources.map((source, index) => (
            <motion.div
              key={index}
