import React from 'react';
import { motion } from 'framer-motion';
import SlackWorkspaceMockup from './SlackWorkspaceMockup'; // << New Import

function HowRelamWorks() {
  const sources = [
    { icon: 'ðŸ“Š', label: 'Property Data' },
    { icon: 'ðŸŒ', label: 'Web Signals' },
    { icon: 'ðŸ§‘â€ðŸ’¼', label: 'Customer Lists' },
    { icon: 'ðŸ“„', label: 'Internal Docs' },
  ];

  return (
    <section id="how-it-works" className="py-32 px-6 relative bg-white overflow-hidden">
      <div className="max-w-6xl mx-auto text-center">

        <h2 className="text-4xl font-bold mb-20 text-[#111827]">How Relam.ai Works</h2>

        {/* Sources */}
        <div className="flex flex-wrap justify-center gap-10 mb-12">
          {sources.map((source, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, scale: 0.9, y: 40 }}
              whileInView={{ opacity: 1, scale: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.1 }}
              whileHover={{ scale: 1.08, rotate: 2 }}
              className="flex flex-col items-center"
            >
              <div className="text-4xl bg-[#f1f5fb] w-16 h-16 flex items-center justify-center rounded-full shadow-md mb-2 hover:shadow-lg hover:bg-[#eef2ff] transition-all">
                {source.icon}
              </div>
              <p className="text-[#4b5563] text-sm font-semibold">{source.label}</p>
            </motion.div>
          ))}
        </div>

        {/* Best-in-Class Section */}
        <motion.div
          initial={{ opacity: 0, scale: 0.9, y: 40 }}
          whileInView={{ opacity: 1, scale: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, ease: 'easeOut' }}
          className="bg-[#ffffff] p-10 rounded-2xl shadow-xl border border-[#e0e7ff] max-w-3xl mx-auto mb-16"
        >
          <h3 className="text-2xl font-bold text-[#111827] mb-4">
            Best-in-class AI and Computational Frameworks
          </h3>
          <hr className="border-[#e0e7ff] my-6" />
          <div className="flex flex-col gap-6 text-left">
            <div className="flex items-center gap-4">
              <span className="text-xl">ðŸŽ¯</span>
              <div>
                <p className="text-lg font-bold text-[#111827]">96% Accuracy</p>
                <p className="text-sm text-[#6b7280]">Validated across proprietary datasets</p>
              </div>
            </div>
            <div className="flex items-center gap-4">
              <span className="text-xl">ðŸ§¹</span>
              <div>
                <p className="text-lg font-bold text-[#111827]">90% Noise Reduction</p>
                <p className="text-sm text-[#6b7280]">Using proprietary computational frameworks</p>
              </div>
            </div>
          </div>
        </motion.div>

        {/* Contextual Insights */}
        <motion.div
          initial={{ opacity: 0, scale: 0.9, y: 40 }}
          whileInView={{ opacity: 1, scale: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, ease: 'easeOut' }}
          className="bg-[#f1f5fb] inline-block px-10 py-8 rounded-2xl shadow-md text-lg font-semibold mb-16"
        >
          Contextual Insights
        </motion.div>

        {/* Slack Mockup */}
        <SlackWorkspaceMockup />

      </div>
    </section>
  );
}

export default HowRelamWorks;
