import React from 'react';
import { motion } from 'framer-motion';

// --- Real SVG Icon Components ---

const SlackIcon = () => (
  <svg viewBox="0 0 122.8 122.8" fill="none" xmlns="http://www.w3.org/2000/svg" className="w-8 h-8">
    <path fill="#36C5F0" d="M30.4 72.4c0 5-4 9-9 9s-9-4-9-9c0-5 4-9 9-9h9v9zm4.5 0c0-5 4-9 9-9s9 4 9 9v22.5c0 5-4 9-9 9s-9-4-9-9V72.4z" />
    <path fill="#2EB67D" d="M50.7 30.4c-5 0-9-4-9-9s4-9 9-9c5 0 9 4 9 9v9h-9zm0 4.5c5 0 9 4 9 9s-4 9-9 9H28.2c-5 0-9-4-9-9s4-9 9-9h22.5z" />
    <path fill="#ECB22E" d="M92.4 50.7c0-5 4-9 9-9s9 4 9 9c0 5-4 9-9 9h-9v-9zm-4.5 0c0 5-4 9-9 9s-9-4-9-9V28.2c0-5 4-9 9-9s9 4 9 9v22.5z" />
    <path fill="#E01E5A" d="M72.1 92.4c5 0 9 4 9 9s-4 9-9 9c-5 0-9-4-9-9v-9h9zm0-4.5c-5 0-9-4-9-9s4-9 9-9h22.5c5 0 9 4 9 9s-4 9-9 9H72.1z" />
  </svg>
);

const CRMIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" className="w-8 h-8 text-orange-500">
    <ellipse cx="12" cy="5" rx="9" ry="3"></ellipse>
    <path d="M3 5v14c0 1.657 4.03 3 9 3s9-1.343 9-3V5"></path>
    <path d="M3 12c0 1.657 4.03 3 9 3s9-1.343 9-3"></path>
  </svg>
);

const APIIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-8 h-8 text-purple-500">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M16 18l6-6-6-6M8 6l-6 6 6 6" />
  </svg>
);

const DashboardIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24" stroke="currentColor" className="w-8 h-8 text-teal-500">
    <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M3 3h6v6H3zM15 3h6v6h-6zM3 15h6v6H3zM15 15h6v6h-6z" />
  </svg>
);

// --- Main Component ---

function HowRelamWorks() {
  const sources = [
    { icon: '📊', label: 'Property Data' },
    { icon: '🌐', label: 'Web Signals' },
    { icon: '🧑‍💼', label: 'Customer Lists' },
    { icon: '📄', label: 'Internal Docs' },
  ];

  const outputs = [
    { icon: <SlackIcon />, label: 'Slack' },
    { icon: <CRMIcon />, label: 'CRM' },
    { icon: <APIIcon />, label: 'API' },
    { icon: <DashboardIcon />, label: 'Dashboard' },
  ];

  return (
    <section id="how-it-works" className="py-32 px-6 relative bg-white overflow-hidden">
      <div className="max-w-6xl mx-auto text-center">

        <h2 className="text-4xl font-bold mb-20 text-[#111827]">How Relam.ai Works</h2>

        {/* SOURCES */}
        <div className="flex flex-wrap justify-center gap-10 mb-12">
          {sources.map((source, index) => (
            <motion.div
              key={index}
              initial={{ opacity: 0, scale: 0.9, y: 40 }}
              whileInView={{ opacity: 1, scale: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.6, delay: index * 0.1, ease: 'easeOut' }}
              whileHover={{ scale: 1.08, rotate: 2 }}
              className="flex flex-col items-center"
            >
              <div className="text-4xl bg-[#f1f5fb] w-20 h-20 flex items-center justify-center rounded-full shadow-md mb-2 hover:shadow-xl hover:bg-[#eef2ff] transition-all">
                <motion.div
                  animate={{ y: [0, -5, 0] }}
                  transition={{ repeat: Infinity, duration: 3, ease: 'easeInOut' }}
                >
                  {source.icon}
                </motion.div>
              </div>
              <p className="text-[#4b5563] text-sm font-semibold">{source.label}</p>
            </motion.div>
          ))}
        </div>

        {/* Connecting Line */}
        <div className="w-1 h-20 mx-auto border-l-2 border-dashed border-[#e0e7ff] mb-8"></div>

        {/* Best in Class Section */}
        <motion.div
          initial={{ opacity: 0, scale: 0.9, y: 40 }}
          whileInView={{ opacity: 1, scale: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.7, ease: 'easeOut' }}
          className="bg-[#ffffff] p-8 rounded-2xl shadow-xl border border-[#e0e7ff] max-w-3xl mx-auto mb-16"
        >
          <h3 className="text-2xl font-bold text-[#111827] mb-8">
            Best-in-class AI and Computational Frameworks
          </h3>

          <div className="flex flex-col md:flex-row items-center justify-between gap-8">
            {/* KPIs */}
            <div className="flex flex-col gap-6 text-left">
              <div className="flex items-center gap-2">
                <span className="text-3xl">🎯</span>
                <div>
                  <p className="text-lg font-bold text-[#111827]">96% Accuracy</p>
                  <p className="text-sm text-[#6b7280]">Validated across property datasets</p>
                </div>
              </div>
              <div className="flex items-center gap-2">
                <span className="text-3xl">🧹</span>
                <div>
                  <p className="text-lg font-bold text-[#111827]">90% Noise Reduction</p>
                  <p className="text-sm text-[#6b7280]">Using property computational frameworks</p>
                </div>
