import React, { useEffect } from 'react';
import './index.css';

function App() {
  useEffect(() => {
    document.documentElement.style.scrollBehavior = 'smooth';
  }, []);

  return (
    <div className="bg-gradient-to-b from-white via-blue-50 to-white min-h-screen text-black font-sans">
      {/* Header */}
      <header className="py-4 px-8 flex items-center justify-between bg-white/80 backdrop-blur-md shadow sticky top-0 z-50">
        <div className="text-2xl font-bold text-blue-700">Relam.ai</div>
        <nav className="hidden md:flex gap-8 text-sm font-medium text-gray-700">
          <a href="#features" className="hover:text-blue-600 transition">Features</a>
          <a href="#market-intelligence" className="hover:text-blue-600 transition">Market Intelligence</a>
          <a href="#usecases" className="hover:text-blue-600 transition">Use Cases</a>
          <a href="#pricing" className="hover:text-blue-600 transition">Pricing</a>
          <a href="#resources" className="hover:text-blue-600 transition">Resources</a>
        </nav>
        <div className="hidden md:flex gap-4">
          <a href="#login" className="text-gray-700 hover:text-blue-700 text-sm">Login</a>
          <a href="#get-started" className="bg-blue-600 text-white px-5 py-2 rounded-lg text-sm font-semibold hover:bg-blue-700 transition">Get Started</a>
        </div>
        {/* Mobile Menu Icon */}
        <div className="md:hidden">
          <button id="menu-button" className="text-blue-700 focus:outline-none">
            <svg className="w-6 h-6" fill="none" stroke="currentColor" viewBox="0 0 24 24">
              <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M4 6h16M4 12h16M4 18h16" />
            </svg>
          </button>
        </div>
      </header>

      {/* Hero Section */}
      <main className="text-center px-6 py-24 max-w-4xl mx-auto">
        <h1 className="text-5xl font-bold text-blue-700">
          Cut through market noise.<br />Deliver competitive insight, instantly.
        </h1>
        <p className="mt-6 text-lg text-gray-700">
          Relam monitors the market and your competitors, learns from your sales decks, and delivers contextual updates where your team already works — in Slack.
        </p>
        <div className="mt-10 flex justify-center gap-4 flex-wrap">
          <a href="#get-started" className="bg-blue-600 text-white px-6 py-3 rounded-xl">Try It Free</a>
          <a href="#video" className="border border-blue-600 text-blue-600 px-6 py-3 rounded-xl">See It in Action →</a>
        </div>
      </main>

      {/* Features Section */}
      <section id="features" className="px-6 py-20 max-w-5xl mx-auto">
        <h2 className="text-3xl font-bold text-center mb-16 text-blue-700">What Makes Relam Different</h2>
        <div className="grid md:grid-cols-3 gap-10 text-center">
          <FeatureCard title="Slack-First Delivery" description="Your GTM team stays in Slack — so do your insights. No new tabs or dashboards." />
          <FeatureCard title="Learns From Internal Docs" description="Relam understands your sales language, competitive slides, and product priorities." />
          <FeatureCard title="Contextual Summarization" description="We explain why updates matter for your deals — not just headlines." />
        </div>
      </section>

      {/* Market Intelligence Section */}
      <section id="market-intelligence" className="px-6 py-20 bg-gradient-to-r from-blue-100 to-blue-50">
        <h2 className="text-3xl font-bold text-center mb-12 text-blue-700">Market Intelligence for Your Industry</h2>
        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto text-left">
          <VerticalCard title="SaaS & Tech" description="Track competitor product launches, funding news, and major shifts." />
          <VerticalCard title="Real Estate & CRE" description="Surface tenant movement, retail trends, and market sentiment signals." />
          <VerticalCard title="Ecommerce & Retail" description="Analyze promotions, inventory trends, and emerging competition." />
          <VerticalCard title="Healthcare & Biotech" description="Monitor M&A activity, clinical trial updates, and innovation news." />
          <VerticalCard title="Finance & Banking" description="Capture fintech innovation, regulatory changes, and new players." />
          <VerticalCard title="Energy & Sustainability" description="Track ESG developments, regulations, and technology disruptions." />
        </div>
      </section>

      {/* Use Cases Section */}
      <section id="usecases" className="bg-blue-50 px-6 py-20">
        <h2 className="text-2xl font-bold text-center mb-10 text-blue-700">Built for Revenue Teams</h2>
        <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
          <UseCaseCard title="CXOs, RevOps, CI Leads" description="Stay ahead of market shifts, key competitor moves, and strategic insights." />
          <UseCaseCard title="Sales, CS, Product Marketing" description="Get contextual battlecards, alerts, and actions for better deals and retention." />
        </div>
      </section>

      {/* Grow Your Business Section */}
      <section id="grow-your-business" className="bg-gradient-to-r from-blue-600 to-blue-800 text-white py-20 px-6 text-center">
        <h2 className="text-3xl font-bold mb-4">Grow Your Business With Relam.ai</h2>
        <p className="text-lg mb-8">Unlock contextual market intelligence tailored to your revenue teams. Faster decisions. Smarter moves.</p>
        <div className="flex justify-center gap-4 flex-wrap">
          <a href="#get-started" className="bg-white text-blue-700 px-6 py-3 rounded-xl font-semibold hover:bg-blue-100 transition-transform transform hover:scale-105">Try it Free</a>
          <a href="#get-demo" className="bg-transparent border border-white px-6 py-3 rounded-xl font-semibold hover:bg-blue-900 transition-transform transform hover:scale-105">Get a Demo</a>
